from time import time
from collections import Counter


class Solution:
    def threeSumMulti(self, arr: list[int], target: int) -> int:

        result = 0
        cntr = Counter(arr)

        for first in list(cntr.keys()):

            num_first = cntr[first]
            del cntr[first]

            if first > target:
                continue

            if first * 3 == target:
                result += num_first * (num_first - 1) * (num_first - 2) // 6  # n*(n-1)*(n-2)/3!

            if 0 <= (target - first * 2) != first:
                need = target - first * 2
                result += cntr[need] * num_first * (num_first - 1) // 2  # n*(n-1)/2!

            need = target - first
            if need % 2 == 0:
                result += num_first * cntr[need//2] * (cntr[need//2] - 1) // 2  # n*(n-1)/2!
            seen = set()
            for key, value in cntr.items():
                seen.add(key)
                if key == first:
                    continue
                need2 = need - key
                if need2 < 0 or need2 in seen:
                    continue
                result += num_first * value * cntr[need2]
        return result % (10**9 + 7)

